mapper.select:
select * 
from user 
where user_id=#{user_id} and user_name=#{user_name}

select user_id 
from user 
where user_name=#{user_name} and user_phone=#{user_phone}

select user_pw 
from user 
where user_id=#{user_id} and user_phone=#{user_phone}

mapper.update:
update user 
set user_pw=#{user_pw} 
where user_id=#{user_id}

update user
		
	user_name=#{user_name},
	user_pw=#{user_pw} ,
	
		latitude =#{latitude},
	
	
		longitude =#{longitude}
					

where user_id=#{user_id}

mapper.select:
select count(*)
from user
where user_id=#{user_id} and user_pw=#{user_pw}

mapper.update:
update user
set roadFullAddr=#{roadFullAddr}
where user_id=#{user_id}
