mapper.select:
select user_id
from user
where user_name=#{user_name} and user_tel=#{user_tel}

select count(*)
from user
where user_id=#{user_id} and user_pw=#{user_pw}

mapper.update:
update user
set user_nick=#{user_name}, user_tel=#{user_tel}, user_email=#{user_email}
where user_id=#{user_id}

mapper.select:
select count(*) FROM user
where user_id = #{user_id}
  and user_pw = #{user_pw}
